import React from 'react';
import { useState } from 'react';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Card from '@material-ui/core/Card';
import List from '@material-ui/core/List';
import Grid from '@material-ui/core/Grid';
import Slider from '@material-ui/core/Slider';
import Gpurpose from './Gpurpose';
import Coptimized from './Coptimized';
import Moptimized from './Moptimized';



function valuetext(value) {
    return `${value}`;
}
export default function HomePage() {
    const [page, setPage] = useState(0);
    const [cpu, setCpu] = useState(1);
    const [mem, setMem] = useState(1);
    const [storage, setStorage] = useState(25);
    const [transfer, setTransfer] = useState(1);
    const [totalStorage, settotalStorage] = useState(0);
    const [stTransfer, setstTransfer] = useState(0);

    const switcher = (e, newVal) => {
        setPage(newVal)
        switch (newVal) {
            case 0:
                setCpu(1)
                setMem(1)
                setStorage(25)
                setTransfer(1)
                break;
            case 1:
                setCpu(2)
                setMem(8)
                setStorage(25)
                setTransfer(4)
                break;
            case 2:
                setCpu(2)
                setMem(16)
                setStorage(50)
                setTransfer(4)
                break;
        }
    }
    const standardConfiguration = (e, val) => {

        switch (val) {
            case 0:
                setCpu(1)
                setMem(1)
                setStorage(25)
                setTransfer(1)
                break;
            case 10:
                setCpu(1)
                setMem(2)
                setStorage(50)
                setTransfer(2)
                break;
            case 20:
                setCpu(2)
                setMem(4)
                setStorage(80)
                setTransfer(4)
                break;

            case 30:
                setCpu(4)
                setMem(8)
                setStorage(160)
                setTransfer(5)
                break;
            case 40:
                setCpu(6)
                setMem(16)
                setStorage(320)
                setTransfer(6)
                break;
            case 50:
                setCpu(8)
                setMem(32)
                setStorage(640)
                setTransfer(7)
                break;
            case 60:
                setCpu(12)
                setMem(48)
                setStorage(960)
                setTransfer(8)
                break;
            case 70:
                setCpu(16)
                setMem(64)
                setStorage(1280)
                setTransfer(29)
                break;
            case 80:
                setCpu(20)
                setMem(96)
                setStorage(1920)
                setTransfer(10)
                break;
            case 90:
                setCpu(24)
                setMem(128)
                setStorage(2560)
                setTransfer(11)
                break;
            case 100:
                setCpu(32)
                setMem(192)
                setStorage(3840)
                setTransfer(12)
                break;

        }
        console.log(val)
    }
    const standardtStorage = (e, val) => {
        switch (val) {
            case 0:
                settotalStorage(0)
                break;
            case 10:
                settotalStorage(1)
                break;
            case 20:
                settotalStorage(2)
                break;
            case 30:
                settotalStorage(3)
                break;
            case 40:
                settotalStorage(4)
                break;
            case 50:
                settotalStorage(5)
                break;
            case 60:
                settotalStorage(6)
                break;
            case 70:
                settotalStorage(7)
                break;
            case 80:
                settotalStorage(8)
                break;
            case 90:
                settotalStorage(9)
                break;
            case 100:
                settotalStorage(10)
                break;
        }
        console.log(val)
    }
    const standardtTransfer = (e, val) => {
        switch (val) {
            case 0:
                setstTransfer(0)
                break;
            case 10:
                setstTransfer(1)
                break;
            case 20:
                setstTransfer(2)
                break;
            case 30:
                setstTransfer(3)
                break;
            case 40:
                setstTransfer(4)
                break;
            case 50:
                setstTransfer(5)
                break;
            case 60:
                setstTransfer(6)
                break;
            case 70:
                setstTransfer(7)
                break;
            case 80:
                setstTransfer(8)
                break;
            case 90:
                setstTransfer(9)
                break;
            case 100:
                setstTransfer(10)
                break;
        }
        console.log(val)
    }



    return (
        <div className="HomePage">
            <Card className="card" my="20px auto">
                <div className="content">
                    <h2>Build your Droplet</h2>
                    <Tabs
                        className="tabs"
                        onChange={switcher}
                        value={page}
                        indicatorColor="primary">
                        <Tab label="Standard" />
                        <Tab label="General Purpose" />
                        <Tab label="Cpu optimized" />
                        <Tab label="memory optimized" />
                    </Tabs>

                    <div className="tabItem">
                        <div className="configuration">
                            <h2>Configuration</h2>
                            <Slider
                                onChange={standardConfiguration}
                                min={0}
                                max={100}
                                step={10}
                                className="configRange"
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider-custom"
                            />

                        </div>
                        <Grid container spacing={0}>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    CPUs <span className="config-info">{cpu}</span>
                                </List>
                            </Grid>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Memory <span className="config-info">{mem} GB</span>
                                </List>
                            </Grid>

                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Storage <span className="config-info">{storage} GB</span>
                                </List>
                            </Grid>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Transfer <span className="config-info">{transfer} TB</span>
                                </List>
                            </Grid>

                        </Grid>
                        <div className="sliderRange">
                            <h2>Total Storage <span>+{totalStorage} TB</span></h2>
                            <Slider
                                onChange={standardtStorage}
                                min={0}
                                max={100}
                                step={10}
                                className="storageRange"
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider-custom"

                            />
                            <h2>Total Transfer <span>+{stTransfer} TB</span></h2>
                            <Slider
                                onChange={standardtTransfer}
                                min={0}
                                max={100}
                                step={10}
                                className="transferRange"
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider-custom"
                            />
                        </div>

                    </div>

                </div>
            </Card>
        </div>
    )
}
