import React from 'react';
import { useState } from 'react';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Card from '@material-ui/core/Card';
import List from '@material-ui/core/List';
import Grid from '@material-ui/core/Grid';
import Slider from '@material-ui/core/Slider';
import Tooltip from "@material-ui/core/Tooltip";
import Paper from "@material-ui/core/Paper";
import { withStyles, makeStyles } from "@material-ui/core/styles";



function valuetext(value) {
    return `${value}`;
}
function Tool({title}){
    return <span>{title}</span>
}
function arrowGenerator(color) {
    return {
        '&[x-placement*="bottom"] $arrow': {
            top: 0,
            left: 0,
            marginTop: "-0.95em",
            width: "2em",
            height: "1em",
            "&::before": {
                borderWidth: "0 1em 1em 1em",
                borderColor: `transparent transparent ${color} transparent`
            }
        },
        '&[x-placement*="top"] $arrow': {
            bottom: 0,
            left: 0,
            marginBottom: "-0.95em",
            width: "2em",
            height: "1em",
            "&::before": {
                borderWidth: "1em 1em 0 1em",
                borderColor: `${color} transparent transparent transparent`
            }
        },
        '&[x-placement*="right"] $arrow': {
            left: 0,
            marginLeft: "-0.95em",
            height: "2em",
            width: "1em",
            "&::before": {
                borderWidth: "1em 1em 1em 0",
                borderColor: `transparent ${color} transparent transparent`
            }
        },
        '&[x-placement*="left"] $arrow': {
            right: 0,
            marginRight: "-0.95em",
            height: "2em",
            width: "1em",
            "&::before": {
                borderWidth: "1em 0 1em 1em",
                borderColor: `transparent transparent transparent ${color}`
            }
        }
    };
}

const useStylesBootstrap = makeStyles(theme => ({
    arrow: {
        position: "absolute",
        fontSize: 8,
        "&::before": {
            content: '""',
            margin: "auto",
            display: "block",
            width: 0,
            height: 0,
            borderStyle: "solid"
        }
    },
    popper: arrowGenerator("#031b4e"),
    tooltip: {
        fontWeight: "bold",
        fontSize: 14,
        padding: "5px 20px",
        position: "relative",
        backgroundColor: "#031b4e"
    },
    tooltipPlacementLeft: {
        margin: "0 8px"
    },
    tooltipPlacementRight: {
        margin: "0 8px"
    },
    tooltipPlacementTop: {
        margin: "8px 0"
    },
    tooltipPlacementBottom: {
        margin: "5px 0"
    }
}));

function ValueLabelComponent(props) {
    const { children, open, value } = props;
    const { arrow, ...classes } = useStylesBootstrap(0);
    const [arrowRef, setArrowRef] = React.useState(null);
    const popperRef = React.useRef(null);

    React.useEffect(() => {
        if (popperRef.current) {
            popperRef.current.update();
        }
    });

    return (
        <Tooltip
            classes={classes}
            PopperProps={{
                popperOptions: {
                    modifiers: {
                        arrow: {
                            enabled: Boolean(arrowRef),
                            element: arrowRef
                        }
                    }
                }
            }}
            {...props}
            open={open}
            enterTouchDelay={0}
            placement="bottom"
            title={
                <React.Fragment>
                    {`+${value} TB`}
                    <span className={arrow} ref={setArrowRef} />
                </React.Fragment>
            }
        >
            {children}
        </Tooltip>
    );
}

export default function HomePage() {
    const [page, setPage] = useState(0);
    const [sliderVal, setSliderVal] = useState(0);
    const [transfer, setTransfer] = useState(0);

    const [sliderMax, setSliderMax] = useState(11);
    const [config, setConfig] = useState({
        cpus: 1,
        mem: 1,
        storage: 25,
        transfer: 1
    });
    const [totalStorage, settotalStorage] = useState(0);


    const packages = {
        standard:
            [{
                cpus: 1,
                mem: 25,
                storage: 1,
                transfer: 1
            },
            {
                cpus: 1,
                mem: 50,
                storage: 2,
                transfer: 2
            },
            {
                cpus: 2,
                mem: 80,
                storage: 4,
                transfer: 4
            },
            {
                cpus: 4,
                mem: 160,
                storage: 8,
                transfer: 5
            },
            {
                cpus: 6,
                mem: 320,
                storage: 16,
                transfer: 6
            },
            {
                cpus: 8,
                mem: 640,
                storage: 32,
                transfer: 7
            },
            {
                cpus: 12,
                mem: 960,
                storage: 48,
                transfer: 8
            },
            {
                cpus: 16,
                mem: 1280,
                storage: 64,
                transfer: 9
            },
            {
                cpus: 20,
                mem: 96,
                storage: 1920,
                transfer: 10
            },
            {
                cpus: 24,
                mem: 128,
                storage: 2560,
                transfer: 11
            },
            {
                cpus: 32,
                mem: 192,
                storage: 3840,
                transfer: 12
            }
            ],
        general_purpose: [
            {
                cpus: 2,
                mem: 8,
                storage: 25,
                transfer: 4
            }, {
                cpus: 4,
                mem: 16,
                storage: 50,
                transfer: 5
            }, {
                cpus: 8,
                mem: 32,
                storage: 100,
                transfer: 6
            }, {
                cpus: 16,
                mem: 64,
                storage: 200,
                transfer: 7
            }, {
                cpus: 32,
                mem: 128,
                storage: 400,
                transfer: 8
            }, {
                cpus: 40,
                mem: 160,
                storage: 500,
                transfer: 9
            }
        ]

    }


    const switcher = (e, newVal) => {
        setSliderVal(0)
        setPage(newVal)
        switch (newVal) {
            case 0:
                setConfig(packages.standard[0])
                break;
            case 1:
                setConfig(packages.general_purpose[0])
                break;
            // case 2:
            //         setConfig(packages.general_purpose[0])

            //     break;
            // 
        }
    }


    const sliderRange = (e, val) => {
        setSliderVal(val)
        switch (page) {
            case 0:
                setSliderMax(11)
                setConfig(packages.standard[0])
                switch (val) {
                    case 0:
                        setConfig(packages.standard[val])
                        break;
                    case 1:
                        setConfig(packages.standard[val])
                        break;
                    case 2:
                        setConfig(packages.standard[val])
                        break;
                    case 3:
                        setConfig(packages.standard[val])
                        break;
                    case 4:
                        setConfig(packages.standard[val])
                        break;
                    case 5:
                        setConfig(packages.standard[val])
                        break;
                    case 6:
                        setConfig(packages.standard[val])
                        break;
                    case 7:
                        setConfig(packages.standard[val])
                        break;
                    case 8:
                        setConfig(packages.standard[val])
                        break;
                    case 9:
                        setConfig(packages.standard[val])
                        break;
                    case 10:
                        setConfig(packages.standard[val])
                        break;
                }
                break;
            case 1:
                setSliderMax(5)
                switch (val) {
                    case 0:
                        setConfig(packages.general_purpose[val])
                        break;
                    case 1:
                        setConfig(packages.general_purpose[val])
                        break;
                    case 2:
                        setConfig(packages.general_purpose[val])
                        break;
                    case 3:
                        setConfig(packages.general_purpose[val])
                        break;
                    case 4:
                        setConfig(packages.general_purpose[val])
                        break;
                    case 5:
                        setConfig(packages.general_purpose[val])
                        break;
                }
                break;
            case 2:

                break;

            case 3:

                break;
            default:
                setConfig(config)
                break;

        }
        console.log(val)
    }

    const standardtStorage = (e, val) => {
        settotalStorage(val)
    }
    const standardTransfer = (e, val) => {
        setTransfer(val)
    }

    return (
        <div className="HomePage">
            <Card className="card" my="20px auto">
                <div className="content">
                    <h2>Build your Droplet</h2>
                    <Tabs
                        className="tabs"
                        onChange={switcher}
                        value={page}
                        indicatorColor="primary">
                        <Tab label="Standard" />
                        <Tab label="General Purpose" />
                        <Tab label="Cpu optimized" />
                        <Tab label="memory optimized" />
                    </Tabs>

                    <div className="tabItem">
                        <div className="configuration">
                            <h2>Configuration</h2>
                            <Slider
                                onChange={sliderRange}
                                value={sliderVal}
                                min={0}
                                max={sliderMax}
                                className="configRange"
                                rail={{ background: 'red', border: '2px solid black' }}
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider-custom"
                            />

                        </div>
                        <Grid container spacing={0}>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    CPUs <span className="config-info">{config.cpus}</span>
                                </List>
                            </Grid>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Memory <span className="config-info">{config.mem} GB</span>
                                </List>
                            </Grid>

                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Storage <span className="config-info">{config.storage} GB</span>
                                </List>
                            </Grid>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Transfer <span className="config-info">{config.transfer} TB</span>
                                </List>
                            </Grid>

                        </Grid>
                        <div className="sliderRange">
                            <h2>Total Storage  <span>+{totalStorage} TB</span> </h2>
                            <Paper>
                            <Slider
                                onChange={standardtStorage}
                                min={0}
                                max={10}
                                step={1}
                                className="storageRange"
                                ValueLabelComponent={ValueLabelComponent}
                                aria-label="custom thumb label"
                            />
                            </Paper>
                            <h2>Total Transfer <span>+{transfer} TB</span></h2>
                            <Slider
                                onChange={standardTransfer}
                                min={0}
                                max={10}
                                marks={false}
                                className="transferRange"
                            //  ValueLabelComponent={ValueLabelComponent}
                            />
                        </div>

                    </div>

                </div>
            </Card>
        </div>
    )
}
