import React from 'react';
import { useState } from 'react';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Card from '@material-ui/core/Card';
import List from '@material-ui/core/List';
import Grid from '@material-ui/core/Grid';
import Slider from '@material-ui/core/Slider';
import Gpurpose from './Gpurpose';
import Coptimized from './Coptimized';
import Moptimized from './Moptimized';



function valuetext(value) {
    return `${value}`;
}
export default function HomePage() {
    const [page, setPage] = useState(0);
    const [sliderMax, setSliderMax] = useState(11);
    const [config, setConfig] = useState({
        cpus: 0,
        mem: 0,
        storage: 0,
        transfer: 0
    });
    const [totalStorage, settotalStorage] = useState(0);
    const [stTransfer, setstTransfer] = useState(0);

    const packages = {
        standard:
            [{
                cpus: 1,
                mem: 25,
                storage: 1,
                transfer: 1
            },
            {
                cpus: 1,
                mem: 50,
                storage: 2,
                transfer: 2
            },
            {
                cpus: 2,
                mem: 80,
                storage: 4,
                transfer: 4
            },
            {
                cpus: 4,
                mem: 160,
                storage: 8,
                transfer: 5
            },
            {
                cpus: 6,
                mem: 320,
                storage: 16,
                transfer: 6
            },
            {
                cpus: 8,
                mem: 640,
                storage: 32,
                transfer: 7
            },
            {
                cpus: 12,
                mem: 960,
                storage: 48,
                transfer: 8
            },
            {
                cpus: 16,
                mem: 1280,
                storage: 64,
                transfer: 9
            },
            {
                cpus: 20,
                mem: 96,
                storage: 1920,
                transfer: 10
            },
            {
                cpus: 24,
                mem: 128,
                storage: 2560,
                transfer: 11
            },
            {
                cpus: 32,
                mem: 192,
                storage: 3840,
                transfer: 12
            }
            ],
        general_purpose: [
            {
                cpus: 1,
                mem: 25,
                storage: 1,
                transfer: 1
            }, {
                cpus: 1,
                mem: 25,
                storage: 1,
                transfer: 1
            }, {
                cpus: 1,
                mem: 25,
                storage: 1,
                transfer: 1
            }, {
                cpus: 1,
                mem: 25,
                storage: 1,
                transfer: 1
            }, {
                cpus: 1,
                mem: 25,
                storage: 1,
                transfer: 1
            }, {
                cpus: 1,
                mem: 25,
                storage: 1,
                transfer: 1
            }
        ]

    }


    const switcher = (e, newVal) => {
        setPage(newVal)
        switch (newVal) {
            case 0:
                setCpu(1)
                setMem(1)
                setStorage(25)
                setTransfer(1)
                break;
            case 1:
                setCpu(2)
                setMem(8)
                setStorage(25)
                setTransfer(4)
                break;
            case 2:
                setCpu(2)
                setMem(16)
                setStorage(50)
                setTransfer(4)
                break;
        }
    }


    const sliderRange = (e, val) => {
        switch (page) {
            case 0:
                setSliderMax(11)
                switch (val) {
                    case 0:
                        setConfig(packages.standard[val])
                        break;
                    case 1:
                        setConfig(packages.standard[val])
                        break;
                    case 2:
                        setConfig(packages.standard[val])
                        break;
                    case 3:
                        setConfig(packages.standard[val])
                        break;
                    case 4:
                        setConfig(packages.standard[val])
                        break;
                    case 5:
                        setConfig(packages.standard[val])
                        break;
                    case 6:
                        setConfig(packages.standard[val])
                        break;
                    case 7:
                        setConfig(packages.standard[val])
                        break;
                    case 8:
                        setConfig(packages.standard[val])
                        break;
                    case 9:
                        setConfig(packages.standard[val])
                        break;
                    case 10:
                        setConfig(packages.standard[val])
                        break;
                }
                break;
            case 1:
                setCpu(1)
                setMem(2)
                setStorage(50)
                setTransfer(2)
                break;
            case 2:
                setCpu(2)
                setMem(4)
                setStorage(80)
                setTransfer(4)
                break;

            case 3:
                setCpu(4)
                setMem(8)
                setStorage(160)
                setTransfer(5)
                break;
        }
        console.log(val)
    }

    const standardtStorage = (e, val) => {
        switch (val) {
            case 0:
                settotalStorage(0)
                break;
            case 10:
                settotalStorage(1)
                break;
            case 20:
                settotalStorage(2)
                break;
            case 30:
                settotalStorage(3)
                break;
            case 40:
                settotalStorage(4)
                break;
            case 50:
                settotalStorage(5)
                break;
            case 60:
                settotalStorage(6)
                break;
            case 70:
                settotalStorage(7)
                break;
            case 80:
                settotalStorage(8)
                break;
            case 90:
                settotalStorage(9)
                break;
            case 100:
                settotalStorage(10)
                break;
        }
        console.log(val)
    }
    const standardtTransfer = (e, val) => {
        switch (val) {
            case 0:
                setstTransfer(0)
                break;
            case 10:
                setstTransfer(1)
                break;
            case 20:
                setstTransfer(2)
                break;
            case 30:
                setstTransfer(3)
                break;
            case 40:
                setstTransfer(4)
                break;
            case 50:
                setstTransfer(5)
                break;
            case 60:
                setstTransfer(6)
                break;
            case 70:
                setstTransfer(7)
                break;
            case 80:
                setstTransfer(8)
                break;
            case 90:
                setstTransfer(9)
                break;
            case 100:
                setstTransfer(10)
                break;
        }
        console.log(val)
    }


    return (
        <div className="HomePage">
            <Card className="card" my="20px auto">
                <div className="content">
                    <h2>Build your Droplet</h2>
                    <Tabs
                        className="tabs"
                        onChange={switcher}
                        value={page}
                        indicatorColor="primary">
                        <Tab label="Standard" />
                        <Tab label="General Purpose" />
                        <Tab label="Cpu optimized" />
                        <Tab label="memory optimized" />
                    </Tabs>

                    <div className="tabItem">
                        <div className="configuration">
                            <h2>Configuration</h2>
                            <Slider
                                onChange={sliderRange}
                                min={0}
                                max={sliderMax}
                                step={.10}
                                className="configRange"
                                rail={{ background: 'red', border: '2px solid black' }}
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider-custom"
                            />

                        </div>
                        <Grid container spacing={0}>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    CPUs <span className="config-info">{config.cpus}</span>
                                </List>
                            </Grid>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Memory <span className="config-info">{config.mem} GB</span>
                                </List>
                            </Grid>

                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Storage <span className="config-info">{config.storage} GB</span>
                                </List>
                            </Grid>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Transfer <span className="config-info">{config.transfer} TB</span>
                                </List>
                            </Grid>

                        </Grid>
                        <div className="sliderRange">
                            <h2>Total Storage <span>+{totalStorage} TB</span></h2>
                            <Slider
                                onChange={standardtStorage}
                                min={0}
                                max={100}
                                step={10}
                                className="storageRange"
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider-custom"

                            />
                            <h2>Total Transfer <span>+{stTransfer} TB</span></h2>
                            <Slider
                                onChange={standardtTransfer}
                                min={0}
                                max={100}
                                step={10}
                                className="transferRange"
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider-custom"
                            />
                        </div>

                    </div>

                </div>
            </Card>
        </div>
    )
}
