import React from 'react';
import { useState } from 'react';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Card from '@material-ui/core/Card';
import List from '@material-ui/core/List';
import Grid from '@material-ui/core/Grid';
import Slider from '@material-ui/core/Slider';



function valuetext(value) {
    return `${value}`;
}
export default function HomePage() {
    const [page, setPage] = useState(0);
    const [cpu, setCpu] = useState(1);
    const [mem, setMem] = useState(1);
    const [storage, setStorage] = useState(25);
    const [transfer, setTransfer] = useState(1);

    const switcher = (e, newVal) => {
        setPage(newVal)
    }
    const configuration = (e, val) => {

        switch (val) {
            case 1:
                setCpu(1)
                setMem(1)
                setStorage(25)
                setTransfer(1)
                break;
            case 10:
                setCpu(1)
                setMem(2)
                setStorage(50)
                setTransfer(2)
                break;
            case 20:
                setCpu(2)
                setMem(4)
                setStorage(80)
                setTransfer(4)
                break;
            case 30:
                setCpu(1)
                setMem(2)
                setStorage(50)
                setTransfer(2)
                break;
            case 40:
                setCpu(4)
                setMem(8)
                setStorage(160)
                setTransfer(5)
                break;
            case 50:
                setCpu(6)
                setMem(16)
                setStorage(320)
                setTransfer(6)
                break;
            case 60:
                setCpu(8)
                setMem(32)
                setStorage(640)
                setTransfer(2)
                break;
            case 70:
                setCpu()
                setMem(2)
                setStorage(50)
                setTransfer(2)
                break;
            case 10:
                setCpu(1)
                setMem(2)
                setStorage(50)
                setTransfer(2)
                break;
            case 10:
                setCpu(1)
                setMem(2)
                setStorage(50)
                setTransfer(2)
                break;
            case 10:
                setCpu(1)
                setMem(2)
                setStorage(50)
                setTransfer(2)
                break;
            case 10:
                setCpu(1)
                setMem(2)
                setStorage(50)
                setTransfer(2)
                break;
        }
        console.log(val)
    }

    return (
        <div className="HomePage">
            <Card className="card" my="20px auto">
                <div className="content">
                    <h2>Build your Droplet</h2>
                    <Tabs
                        className="tabs"
                        onChange={switcher}
                        value={page}
                        indicatorColor="primary">
                        <Tab label="Standard" />
                        <Tab label="General Purpose" />
                        <Tab label="Cpu optimized" />
                        <Tab label="memory optimized" />
                    </Tabs>

                    {page === 0 && <div className="tabItem">
                        <div className="configuration">
                            <h2>Configuration</h2>
                            <Slider
                                onChange={configuration}
                                min={1}
                                max={100}
                                step={10}
                                className="configRange"
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider-custom"
                            />

                        </div>
                        <Grid container spacing={0}>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    CPUs:<span className="config-info">{cpu}</span>
                                </List>
                            </Grid>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Memory:<span className="config-info">{mem} GB</span>
                                </List>
                            </Grid>

                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Storage:<span className="config-info">{storage} GB</span>
                                </List>
                            </Grid>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Transfer:<span className="config-info">{transfer} TB</span>
                                </List>
                            </Grid>

                        </Grid>
                        <div className="sliderRange">
                            <h2>Total Storage</h2>
                            <Slider

                                min={1}
                                max={100}
                                step={10}
                                className="storageRange"
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider-custom"
                            />
                            <h2>Total Transfer</h2>
                            <Slider

                                min={1}
                                max={100}
                                step={10}
                                className="transferRange"
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider-custom"
                            />
                        </div>

                    </div>}

                    {page === 1 && <div>
                        tow
                        </div>}
                    {page === 2 && <div>
                        three
                        </div>}
                    {page === 3 && <div>
                        four
                            </div>}
                </div>
            </Card>
        </div>
    )
}
