import React from 'react';
import { useState } from 'react';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Card from '@material-ui/core/Card';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Grid from '@material-ui/core/Grid';
import { spacing } from '@material-ui/system';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/core/Slider';


function valuetext(value) {
    return `${value}`;
}
export default function HomePage() {
    const [page, setPage] = useState(0);
//functional comnent banaisos..good
    const switcher = (e, newVal) => {
        setPage(newVal)
        console.log(e.target, newVal)
    }


    return (
// range er tooltip pase keno...ota remove kore dibo
        <div className="HomePage">
            <Card className="card" my="20px auto">
                <div className="content">
                    <h2>Build Your Droplet</h2>
                    <Tabs 
                        className="tabs" 
                        onChange={switcher} 
                        value={page}
                        indicatorColor="primary">
                        <Tab label="Standard" />
                        <Tab label="General Purpose" />
                        <Tab label="Cpu optimized" />
                        <Tab label="memory optimized" />
                    </Tabs>

                    {page === 0 && <div className="tabItem">
                        <div className="configuration">
                            <h2>Configuration</h2>
                            <Slider className="configRange"
                                marks="false"
                                defaultValue={0}
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider-custom"
                                step={10}
                                valueLabelDisplay="auto"

                            />
                        </div>
                        <Grid container spacing={0}>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    CPUs:<span className="config-info">2</span>
                                </List>
                            </Grid>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Memory:<span className="config-info">1 GB</span>
                                </List>
                            </Grid>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Storage:<span className="config-info">25 GB</span>
                                </List>
                            </Grid>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Transfer:<span className="config-info">1 TB</span>
                                </List>
                            </Grid>
                        </Grid>
                        <div className="sliderRange">
                            <h2>Total Storage</h2>
                            <Slider className="storageRange "
                                defaultValue={0}
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider-custom"
                                step={10}
                                valueLabelDisplay="auto"

                            />
                            <h2>Total Transfer</h2>
                            <Slider className="transferRange"
                                defaultValue={0}
                                getAriaValueText={valuetext}
                                aria-labelledby="discrete-slider-custom"
                                step={10}
                                valueLabelDisplay="auto"

                            />
                        </div>

                    </div>}

                    {page === 1 && <div>
                        tow
                        </div>}
                    {page === 2 && <div>
                        three
                        </div>}
                    {page === 3 && <div>
                        four
                            </div>}
                </div>
            </Card>
        </div>
    )
}
