import React from 'react';
import { useState } from 'react';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Card from '@material-ui/core/Card';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Grid from '@material-ui/core/Grid';
import { spacing } from '@material-ui/system';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/core/Slider';

export default function HomePage() {
    const [page, setPage] = useState(0);

   const switcher = (e, newVal) => {
        setPage(newVal)
        console.log(e.target, newVal)
    }
    const marks = [
        {
          value: 0,
          label: '0째C',
        },
        {
          value: 20,
          label: '20째C',
        },
        {
          value: 37,
          label: '37째C',
        },
        {
          value: 100,
          label: '100째C',
        },
      ];
    function valuetext(value) {
        return `${value}째C`;
      }

    return (

        <div className="HomePage">
            <Card className="card" my="20px auto">
                <div className="content">
                    <h2>Build Your Droplet</h2>

                    <Tabs className="tabs" onChange={switcher} value={page}>
                        <Tab label="Standard" />
                        <Tab label="General Purpose" />
                        <Tab label="Cpu optimized" />
                        <Tab label="memory optimized" />
                    </Tabs>

                    {page === 0 && <div className="tabItem">
                        <h2>Configuration</h2>
                        <div className={classes.margin} />
      <Typography id="discrete-slider-custom" gutterBottom>
        Custom marks
      </Typography>
      <Slider
        defaultValue={20}
        getAriaValueText={valuetext}
        aria-labelledby="discrete-slider-custom"
        step={10}
        valueLabelDisplay="auto"
        marks={marks}
      />
                        <Grid container spacing={0}>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    CPUs:<span className="config-info">2</span>
                                </List>
                            </Grid>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Memory:<span className="config-info">1 GB</span>
                                </List>
                            </Grid>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Storage:<span className="config-info">25 GB</span>
                                </List>
                            </Grid>
                            <Grid item xs={6}>
                                <List className="configDetails">
                                    Transfer:<span className="config-info">1 TB</span>
                                </List>
                            </Grid>
                        </Grid>

                    </div>}

                    {page === 1 && <div>
                        tow
                        </div>}
                    {page === 2 && <div>
                        three
                        </div>}
                    {page === 3 && <div>
                        four
                            </div>}
                </div>
            </Card>
        </div>
    )
}
